select

,

,

from devicewithuser v inner join device j on v.deviceid = j.id inner
join user u
on
v.userid = u.id
where v.deviceid in (select id from device


		and companyid=#{companyId}


)

	and u.username like #{username}


	and v.userid = #{userId}


	and j.name like #{jobname}


	and u.realname like #{realname}


	order by ${sort} ${order}


	limit #{start},#{size}

select count(*)
from devicewithuser v inner join device j on v.deviceid = j.id
inner
join user u
on v.userid = u.id
where v.deviceid in (select id from device


		and companyid=#{companyId}



)

	and u.username=#{username}


	and v.userid = #{userId}


	and j.name like #{jobname}


	and u.realname like #{realname}

select count(*)
from devicewithuser v inner join device j on v.deviceid = j.id
inner
join user u
on v.userid = u.id
where v.deviceid in (select id from device


		and companyid=#{companyId}


) and v.success=1

	and v.userid = #{userId}


	and j.name like #{jobname}


	and u.username like #{username}


	and u.realname like #{realname}

select

,

,

,com.name as comName
from devicewithuser v
inner join device j on v.deviceid = j.id
inner join user u on v.userid = u.id
inner join company com on j.companyid= com.id
where v.deviceid in (select id from device


		and companyid=#{companyId}


)

		and com.name in(select name from company where id = #{companyId})


and v.success=1

	and v.userid = #{userId}


	and j.name like #{jobname}


	and u.username like #{username}


	and u.realname like #{realname}


	order by ${sort} ${order}


	limit #{start},#{size}

